public class SimpleTest11 {
	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 5, j = 5;
		System.out.println("첫번째i = "+ ++i);
		System.out.println("첫번째j = "+ j++);
		
		System.out.println("두번째i = "+ i);
		System.out.println("두번째j = "+ j);
	}

}
